function getSalary($num,$salaryList){
    if(!is_numeric($num)) return 0;
    foreach($salaryList as $k=>$v){
        $list = explode('-', $v);
        if(isset($list[0]) && isset($list[1])){
            preg_match('/[0-9]+/', $list[1],$str);
            if($num>$list[0] && $num<$str[0]){
                return $k;
            }
        }else{
            preg_match('/[0-9]+/', $list[0],$str);
            if(strpos($list[0], '以下') !==false){
                if($num < $str[0]) return $k;
            }else{
                if($num > $str[0]) return $k;
            }
        }
    }
}

