    $provices = array_keys($provinceCityList);
    $i = 99;
    foreach($provices as $v){
        $pos = strpos($str,$v);
        if($pos!==false && $pos<$i){
            $data['provice'] = $v;
            $i = $pos;
        }
    }unset($i);

    if($data['provice']){
        $city = $provinceCityList[$data['provice']];
    }
    $str = strReplaceOccurance($data['provice'],'',$str,1);
    if($city)
    foreach($city as $v){
        $ci = isset($list[$v])?$list[$v]:$v;
        if(is_array($ci)){
