                if(strpos($str,$va)!==false){
                    if($v=='襄阳') $v='襄樊';
                    if($v=='淮安') $v='淮阴';
                    $data['city'] = $v;
                }
            }
        }else{
            if(strpos($str,$ci)!==false){
                if($v=='襄阳') $v='襄樊';
                if($v=='淮安') $v='淮阴';
                $data['city'] = $v;
            }
        }unset($ci);
    }unset($list);
    $data['provice'] = isset($data['provice'])?$data['provice']:0;
    $data['city'] = isset($data['city'])?$data['city']:0;
    return $data;
}
function strReplaceOccurance($search, $replace, $subject, $occurance) {
   $pos = 0;
   for ($i = 0; $i <= $occurance; $i++) {
       $pos = strpos($subject, $search, $pos);
   }
   return substr_replace($subject, $replace, $pos, strlen($search));
}

