      $imgurl = $new_filedir . $uidmd5 . '_' . $style . '.jpg';
    }
    return $imgurl;
}

function cropImage($src,$dst,$link=36){
	if(!is_file($src)) return null;
	list($width,$height,$type) = getimagesize($src);
	$oldheight = $height-$link;
	$new = imagecreatetruecolor($width, $height);
	switch($type){
		case IMAGETYPE_JPEG:
			$old = imagecreatefromjpeg($src);
			break;
		case IMAGETYPE_PNG :
			$old = imagecreatefrompng($src);
			break;
		case IMAGETYPE_GIF :
			$old = imagecreatefromgif($src);
			break;
	}
	imagecopyresized($new,$old,0,0,0,0,$width,$height,$width,$oldheight);
	switch($type){
		case IMAGETYPE_JPEG:
			imagejpeg($new,$dst);
			break;
		case IMAGETYPE_PNG :
			imagepng($new,$dst);
			break;
		case IMAGETYPE_GIF :
			imagegif($new,$dst);
			break;
	}
	imagedestroy($new);
	imagedestroy($old);
}

function run($arg='collect'){
    if(empty($arg)) exit('no arg!');
    $configs = parse_ini_file('script_config.ini',true);
    if(empty($configs['scripts'][$arg])){
        exit($arg.' stop!');
    }
    unset($arg,$configs);
}

