    global $_MooClass,$dbTablePre;
    $fields = implode(',', array_keys($data));
    $values = '';
    foreach($data as $v){
        $values .= is_numeric($v)?$v.',':"'".$v."',";
    }
    $values = substr($values, 0, -1);
    $sql = "insert into {$dbTablePre}{$table} ({$fields}) values ($values)";
    $_MooClass['MooMySQL']->query($sql);
    $id = $_MooClass['MooMySQL']->insertId($sql);
    if(empty($id)){
        MooWriteFile(dirname(__file__).'/'.$table.'.log',$sql."\r\n",'a+');
    }
    $_MooClass['MooMySQL']->close();
    unset($table,$data,$fields,$values,$_MooClass,$dbTablePre);
    return $id;
}

function dbUpdate($table,$data,$keys){
    global $_MooClass,$dbTablePre;
    if(empty($table) || array_empty($data) || array_empty($keys)) return false;
    $upStr = '';
    foreach($data as $k=>$v){
        $upStr .= $k.' = '.(is_numeric($v)?$v:"'{$v}'").',';
    }
    $upStr = substr($upStr, 0, -1);
    $where = ' where';
    foreach($keys as $k =>$v){
        $where .= " $k = ".(is_numeric($v)?$v:"'{$v}'"). ' and';
    }
    $where = substr($where, 0, -4);
    $_MooClass['MooMySQL']->query("update {$dbTablePre}{$table} set {$upStr} {$where}");
    $_MooClass['MooMySQL']->close();
    unset($table,$data,$keys,$upStr,$where,$_MooClass,$dbTablePre);
}

function dbHas($table,$keys){
    global $_MooClass,$dbTablePre;
    if(empty($table) || array_empty($keys)) return false;
    $where = ' where';
    foreach($keys as $k => $v){
        $where .= " $k = ".(is_numeric($v)?$v:"'{$v}'"). ' and';
    }
    $where = substr($where, 0, -4);
    $num = $_MooClass['MooMySQL']->getOne("select count(*) as c from {$dbTablePre}{$table} {$where}");
    $_MooClass['MooMySQL']->close();
    unset($_MooClass,$dbTablePre,$table,$keys,$where);
    if($num['c']>0)
        return true;
    else
        return false;
}

function my_unserialize($str){
	return unserialize(preg_replace('!s:(\d+):"(.*?)";!se', "'s:'.strlen('$2').':\"$2\";'", $str ));
}

